var Agenda = require('agenda');
var agenda = new Agenda({db: { address: 'mongodb://localhost/agenda-example', collection: 'agendaJobs' }});

// set up agenda
agenda.processEvery('1 minute');
exports.runJob = function() {
	agenda.define('show message', function(job, done) {
    	console.log("Shows message.");
	  	done();
	});

	//agenda.every('5 seconds', 'show message');
	agenda.now('show message');

	agenda.start();
};